import React, { useState, useEffect } from 'react';
import './Modal.css';

const Modal = ({ job, onClose, onUpdate, onDelete }) => {
  const [title, setTitle] = useState(job.title);
  const [description, setDescription] = useState(job.description);
  const [client, setClient] = useState(job.client);
  const [status, setStatus] = useState(job.status);
  const [whatsappNumber, setWhatsappNumber] = useState(job?.whatsapp_number || '');
  const [email, setEmail] = useState(job?.email || '');
  const [file, setFile] = useState(null);

  useEffect(() => {
    if (job) {
      setTitle(job.title);
      setDescription(job.description);
      setClient(job.client);
      setStatus(job.status);
      setWhatsappNumber(job.whatsapp_number || '');
      setEmail(job.email || '');
    }
  }, [job]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedJob = {
      ...job,
      title,
      description,
      client,
      status,
      whatsapp_number: whatsappNumber,
      email,
      file,
    };

    onUpdate(updatedJob);
    onClose();
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDelete = async () => {
    const confirm = window.confirm("Tem certeza de que deseja excluir este job?");
    if (!confirm) return;

    try {
      const response = await fetch(`http://localhost:8000/api/jobs/${job.id}/delete/`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Job excluído com sucesso!");
        onDelete(job.id); // Notifica o componente pai para remover o job da lista
        onClose(); // Fecha o modal
      } else {
        alert("Erro ao excluir o job.");
      }
    } catch (error) {
      console.error("Erro ao excluir o job:", error);
      alert("Erro ao tentar excluir o job.");
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>Editar Job</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            placeholder="Título"
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            placeholder="Descrição"
          />
          <input
            type="text"
            value={client}
            onChange={(e) => setClient(e.target.value)}
            required
            placeholder="Cliente"
          />
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="pending">Pendente</option>
            <option value="approved">Aprovado</option>
            <option value="rejected">Rejeitado</option>
          </select>

          <input
            type="text"
            value={whatsappNumber}
            onChange={(e) => setWhatsappNumber(e.target.value)}
            placeholder="Número de WhatsApp"
          />

          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
          />

          <input type="file" onChange={handleFileChange} />

          <button type="submit">Salvar</button>
        </form>
        <button className="delete-button" onClick={handleDelete}>Excluir Job</button>
        <button className="close-button" onClick={onClose}>Fechar</button>
      </div>
    </div>
  );
};

export default Modal;
