import React, { useState } from 'react';

const Modal = ({ job, onClose, onUpdate }) => {
  const [title, setTitle] = useState(job.title);
  const [description, setDescription] = useState(job.description);
  const [client, setClient] = useState(job.client);
  const [status, setStatus] = useState(job.status); // Adicionado

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate({ ...job, title, description, client, status }); // Enviar o status também
    onClose();
  };

  return (
    <div>
      <h2>Editar Job</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Título"
          required
        />
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Descrição"
          required
        />
        <input
          type="text"
          value={client}
          onChange={(e) => setClient(e.target.value)}
          placeholder="Cliente"
          required
        />
        
        {/* Campo para selecionar o status */}
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="pending">Pendente</option>
          <option value="approved">Aprovado</option>
          <option value="rejected">Rejeitado</option>
        </select>

        <button type="submit">Atualizar Job</button>
      </form>
      <button onClick={onClose}>Fechar</button>
    </div>
  );
};

export default Modal;
