import React, { useState, useEffect } from 'react';
import './Modal.css'; // Estilos do modal

const Modal = ({ isOpen, onClose, onSave, job }) => {
  const [title, setTitle] = useState(job.title);
  const [description, setDescription] = useState(job.description);
  const [client, setClient] = useState(job.client);

  useEffect(() => {
    if (job) {
      setTitle(job.title);
      setDescription(job.description);
      setClient(job.client);
    }
  }, [job]);

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ id: job.id, title, description, client }); // Enviar os dados atualizados
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Editar Job</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)} // Atualiza o título
            placeholder="Título"
            required
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)} // Atualiza a descrição
            placeholder="Descrição"
            required
          />
          <input
            type="text"
            value={client}
            onChange={(e) => setClient(e.target.value)} // Atualiza o cliente
            placeholder="Cliente"
            required
          />
          <button type="submit">Salvar</button>
          <button type="button" onClick={onClose}>Fechar</button>
        </form>
      </div>
    </div>
  );
};

export default Modal;
