import "./JobForm.css"
import React, { useState } from 'react';

const JobForm = ({ onCreate, jobs, setJobs }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [client, setClient] = useState('');
  const [status, setStatus] = useState('pending');
  const [whatsappNumber, setWhatsappNumber] = useState('');
  const [email, setEmail] = useState('');
  const [file, setFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [successMessage, setSuccessMessage] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      setFilePreview(URL.createObjectURL(selectedFile));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!whatsappNumber || !email) {
      alert('Por favor, preencha os campos obrigatórios: WhatsApp e Email.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('description', description);
      formData.append('client', client);
      formData.append('status', status);
      formData.append('whatsapp_number', whatsappNumber);
      formData.append('email', email);
      if (file) {
        formData.append('file', file);
      }

      const response = await fetch('http://localhost:8000/api/jobs/create/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) throw new Error('Erro ao criar o job.');

      const newJob = await response.json();

      // Atualiza a lista de jobs no pai sem recarregar a página
      setJobs((prevJobs) => [...prevJobs, newJob].sort((a, b) => a.status.localeCompare(b.status)));

      resetForm();
      setSuccessMessage(true);
    } catch (error) {
      console.error('Erro ao criar o job:', error);
    }
  };

  const resetForm = () => {
    setTitle('');
    setDescription('');
    setClient('');
    setStatus('pending');
    setWhatsappNumber('');
    setEmail('');
    setFile(null);
    setFilePreview(null);
  };

  return (
    <div>
      {!successMessage ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Título"
            required
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Descrição"
            required
          />
          <input
            type="text"
            value={client}
            onChange={(e) => setClient(e.target.value)}
            placeholder="Cliente"
            required
          />
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="pending">Pendente</option>
            <option value="approved">Aprovado</option>
            <option value="rejected">Rejeitado</option>
          </select>
          <input
            type="text"
            value={whatsappNumber}
            onChange={(e) => setWhatsappNumber(e.target.value)}
            placeholder="Número de WhatsApp"
            required
          />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
          <input type="file" onChange={handleFileChange} />
          {filePreview && (
            <div className="file-preview">
              <strong>Pré-visualização do arquivo:</strong>
              <img
                src={filePreview}
                alt="Preview"
                style={{ width: '100px', height: '100px', marginTop: '10px' }}
              />
            </div>
          )}
          <button type="submit">Criar Job</button>
        </form>
      ) : (
        <div className="success-message">
          <h3>Job criado com sucesso!</h3>
          <button onClick={() => setSuccessMessage(false)}>Criar novo job</button>
          <button onClick={() => onCreate()}>Ver lista de jobs</button>
        </div>
      )}
    </div>
  );
};

export default JobForm;
