import React, { useState, useEffect } from 'react';
import './JobList.css';
import Modal from './Modal';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [editingJob, setEditingJob] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [client, setClient] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetch('http://localhost:8000/api/jobs/')
      .then(response => response.json())
      .then(data => {
        setJobs(data);
      })
      .catch(error => console.error('Error fetching jobs:', error));
  }, []);

  const handleEdit = (job) => {
    setEditingJob(job);
    setTitle(job.title);
    setDescription(job.description);
    setClient(job.client);
    setIsModalOpen(true);
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/api/jobs/${id}/`, {
      method: 'DELETE',
    })
      .then(() => {
        const remainingJobs = jobs.filter(job => job.id !== id);
        setJobs(remainingJobs);
      })
      .catch(error => console.error('Error deleting job:', error));
  };

  const handleUpdate = (id) => {
    fetch(`http://localhost:8000/api/jobs/${id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title, description, client }),
    })
      .then(response => response.json())
      .then(updatedJob => {
        const updatedJobs = jobs.map(job => (job.id === updatedJob.id ? updatedJob : job));
        setJobs(updatedJobs);
        setIsModalOpen(false);
        setEditingJob(null);
        setTitle('');
        setDescription('');
        setClient('');
      })
      .catch(error => console.error('Error updating job:', error));
  };

  return (
    <div>
      <h1>Jobs List</h1>
      <ul>
        {jobs.map((job) => (
          <li key={job.id}>
            {job.title}
            <div className="button-container">
              <button onClick={() => handleEdit(job)}>Editar</button>
              <button onClick={() => handleDelete(job.id)}>Deletar</button>
            </div>
          </li>
        ))}
      </ul>

      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleUpdate}
        job={editingJob}
        setTitle={setTitle}
        setDescription={setDescription}
        setClient={setClient}
      />
    </div>
  );
};

export default JobList;
