import React, { useState } from 'react';

const JobForm = ({ onCreate }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [client, setClient] = useState('');
  const [status, setStatus] = useState('pending');
  const [whatsappNumber, setWhatsappNumber] = useState('');
  const [email, setEmail] = useState('');
  const [file, setFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    // Gerar uma URL para pré-visualização da imagem
    if (selectedFile) {
      setFilePreview(URL.createObjectURL(selectedFile));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validar campos obrigatórios
    if (!whatsappNumber || !email) {
      alert('Por favor, preencha os campos obrigatórios: WhatsApp e Email.');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('client', client);
    formData.append('status', status);
    formData.append('whatsapp_number', whatsappNumber);
    formData.append('email', email);
    if (file) {
      formData.append('file', file);
    }

    fetch('http://localhost:8000/api/jobs/create/', {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao criar o job.');
        }
        return response.json();
      })
      .then((data) => {
        onCreate(data); // Atualiza a lista de jobs no componente pai
        // Limpar os campos após o envio
        setTitle('');
        setDescription('');
        setClient('');
        setStatus('pending');
        setWhatsappNumber('');
        setEmail('');
        setFile(null);
        setFilePreview(null);
      })
      .catch((error) => console.error('Erro ao criar o job:', error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Título"
        required
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Descrição"
        required
      />
      <input
        type="text"
        value={client}
        onChange={(e) => setClient(e.target.value)}
        placeholder="Cliente"
        required
      />
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="pending">Pendente</option>
        <option value="approved">Aprovado</option>
        <option value="rejected">Rejeitado</option>
      </select>
      <input
        type="text"
        value={whatsappNumber}
        onChange={(e) => setWhatsappNumber(e.target.value)}
        placeholder="Número de WhatsApp"
        required
      />
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        required
      />
      <input type="file" onChange={handleFileChange} />
      {filePreview && (
        <div className="file-preview">
          <strong>Pré-visualização do arquivo:</strong>
          <img
            src={filePreview}
            alt="Preview"
            style={{ width: '100px', height: '100px', marginTop: '10px' }}
          />
        </div>
      )}
      <button type="submit">Criar Job</button>
    </form>
  );
};

export default JobForm;
