import React, { useState, useEffect } from 'react';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [editingJob, setEditingJob] = useState(null);
  const [title, setTitle] = useState('');

  useEffect(() => {
    fetch('http://localhost:8000/api/jobs/')
      .then(response => response.json())
      .then(data => {
        setJobs(data);
      })
      .catch(error => console.error('Error fetching jobs:', error));
  }, []);

  const handleEdit = (job) => {
    setEditingJob(job);
    setTitle(job.title);
  };

  const handleUpdate = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8000/api/jobs/${editingJob.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title }),
    })
    .then(response => response.json())
    .then(updatedJob => {
      const updatedJobs = jobs.map(job => (job.id === updatedJob.id ? updatedJob : job));
      setJobs(updatedJobs);
      setEditingJob(null);
      setTitle('');
    })
    .catch(error => console.error('Error updating job:', error));
  };

  return (
    <div>
      <h1>Jobs List</h1>
      <ul>
        {jobs.map(job => (
          <li key={job.id}>
            {job.title} <button onClick={() => handleEdit(job)}>Editar</button>
          </li>
        ))}
      </ul>

      {editingJob && (
        <form onSubmit={handleUpdate}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Editar tÃ­tulo do job"
            required
          />
          <button type="submit">Atualizar Job</button>
        </form>
      )}
    </div>
  );
};

export default JobList;
