// JobList.js
import React, { useState, useEffect } from 'react';
import JobCard from './JobCard'; 
import './JobList.css'; 

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:8000/api/jobs/');
        if (!response.ok) throw new Error('Erro ao carregar jobs.');
        
        const data = await response.json();
        setJobs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  // Função para ordenar os jobs por status
  const sortJobsByStatus = (jobs) => {
    const order = { pending: 1, approved: 2, rejected: 3 };
    return jobs.sort((a, b) => order[a.status] - order[b.status]);
  };

  // Renderiza conteúdo baseado no estado
  if (loading) return <div>Carregando jobs...</div>;
  if (error) return <div>Erro: {error}</div>;

  // Ordena os jobs antes de renderizar
  const sortedJobs = sortJobsByStatus(jobs);

  return (
    <div className="job-list-container">
      <h1>Lista de Jobs</h1>
      <div className="job-list">
        {sortedJobs.map((job) => (
          <JobCard
            key={job.id}
            job={job}
            onEdit={() => {/* Lógica para editar */}}
            onArchive={() => {/* Lógica para arquivar */}}
          />
        ))}
      </div>
    </div>
  );
};

export default JobList;
