import React, { useState, useEffect } from 'react';
import Modal from './Modal';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [editingJob, setEditingJob] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/api/jobs/')
      .then(response => response.json())
      .then(data => setJobs(data))
      .catch(error => console.error('Error fetching jobs:', error));
  }, []);

  const handleUpdate = (updatedJob) => {
    fetch(`http://localhost:8000/api/jobs/${updatedJob.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedJob),
    })
    .then(response => response.json())
    .then(job => {
      const updatedJobs = jobs.map(j => (j.id === job.id ? job : j));
      setJobs(updatedJobs);
    })
    .catch(error => console.error('Error updating job:', error));
  };

  return (
    <div>
      <h1>Jobs List</h1>
      <ul>
        {jobs.map(job => (
          <li key={job.id}>
            {job.title}
            <button onClick={() => setEditingJob(job)}>Editar</button>
          </li>
        ))}
      </ul>
      {editingJob && (
        <Modal job={editingJob} onClose={() => setEditingJob(null)} onUpdate={handleUpdate} />
      )}
    </div>
  );
};

export default JobList;
